        <script>
        // Function to update the current time and display greeting
        function updateTimeAndGreeting() {
            // Create a new Date object
            var now = new Date();

            // Get the current hour
            var hours = now.getHours();

            // Determine the appropriate greeting based on the hour
            var greeting;
            if (hours >= 5 && hours < 12) {
                greeting = "Good Morning";
            } else if (hours >= 12 && hours < 18) {
                greeting = "Good Afternoon";
            } else {
                greeting = "Good Evening";
            }

            // Get the current hour, minute, and second
            var minutes = now.getMinutes();
            var seconds = now.getSeconds();

            // Pad single digit numbers with leading zeros
            hours = String(hours).padStart(2, '0');
            minutes = String(minutes).padStart(2, '0');
            seconds = String(seconds).padStart(2, '0');

            // Display the greeting and current time
            document.getElementById('greeting').textContent = greeting;
            document.getElementById('current-time').textContent = hours + ':' + minutes + ':' + seconds;
        }

        // Call updateTimeAndGreeting() initially to display the greeting and current time
        updateTimeAndGreeting();

        // Call updateTimeAndGreeting() every second to update the displayed time and greeting
        setInterval(updateTimeAndGreeting, 1000);
        </script>
